// SPDX-License-Identifier: MIT
pragma solidity 0.7.1;

import "hardhat-deploy/solc_0.7/proxy/Proxied.sol";
import "./utils/SafeMath.sol";
import "hardhat/console.sol";
import "./Creator.sol";

contract CreatonFactory is Proxied {
    using SafeMath for uint256;
    // -----------------------------------------
    // Events
    // -----------------------------------------

    event CreatorDeployed(
        address user, 
        address creatorContract, 
        string title, 
        string avatarURL, 
        uint256 subscriptionPrice);

    // -----------------------------------------
    // Storage
    // -----------------------------------------

    mapping(address => address) public creatorContracts;

    // -----------------------------------------
    // Constructor
    // -----------------------------------------

    constructor() {}

    // -----------------------------------------
    // External Functions
    // -----------------------------------------

    function deployCreator(
        string calldata avatarURL,
        string calldata title, 
        uint256 subscriptionPrice) external {

        Creator creatorContract = new Creator();
        address creatorContractAddr = address(creatorContract);
        creatorContract.init(
            avatarURL,
            title, 
            subscriptionPrice);
        creatorContracts[msg.sender] = creatorContractAddr;

        emit CreatorDeployed(
            msg.sender, 
            creatorContractAddr, 
            title, 
            avatarURL,
            subscriptionPrice
        );
    }
}

hardhat-deploy/solc_0.7/proxy/Proxied.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

abstract contract Proxied {
    modifier proxied() {
        address adminAddress = _admin();
        if (adminAddress == address(0)) {
            // ensure can not be called twice when used outside of proxy : no admin
            // solhint-disable-next-line security/no-inline-assembly
            assembly {
                sstore(
                    0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103,
                    0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF
                )
            }
        } else {
            require(msg.sender == adminAddress);
        }
        _;
    }

    modifier onlyProxyAdmin() {
        require(msg.sender == _admin(), "NOT_AUTHORIZED");
        _;
    }

    function _admin() internal view returns (address adminAddress) {
        // solhint-disable-next-line security/no-inline-assembly
        assembly {
            adminAddress := sload(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103)
        }
    }
}

