src/superfluid/apps/MultiFlowApp.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.1;

import {
    ISuperfluid,
    ISuperToken,
    SuperAppBase,
    SuperAppDefinitions
} from "./SuperAppBase.sol";
import { IConstantFlowAgreementV1 } from "../interfaces/agreements/IConstantFlowAgreementV1.sol";

// FIXME Create and use a SuperAppBase abstract contract,
//       which implements all the callbacks as reverts.
//       - Revert(callback not implemented constant string)
//FIXME - MsgSender can be different from flowSender
contract MultiFlowsApp is SuperAppBase {

    struct ReceiverData {
        address to;
        uint256 proportion;
    }

    IConstantFlowAgreementV1 internal _constantFlow;
    ISuperfluid internal _host;
    //Sender => To / Proportion
    mapping(address => ReceiverData[]) internal _userFlows;

    constructor(IConstantFlowAgreementV1 constantFlow, ISuperfluid superfluid) {
        require(address(constantFlow) != address(0), "MFA: can't set zero address as constant Flow");
        require(address(superfluid) != address(0), "MFA: can't set zero address as Superfluid");
        _constantFlow = constantFlow;
        _host = superfluid;

        uint256 configWord =
            SuperAppDefinitions.TYPE_APP_FINAL |
            SuperAppDefinitions.BEFORE_AGREEMENT_CREATED_NOOP |
            SuperAppDefinitions.BEFORE_AGREEMENT_TERMINATED_NOOP;

        _host.registerApp(configWord);
    }

    function createMultiFlows(
        ISuperToken superToken,
        address[] calldata receivers,
        uint256[] calldata proportions,
        bytes calldata ctx
    )
        external
        returns(bytes memory newCtx)
    {
        require(msg.sender == address(_host), "MFA: Only official superfluid host is supported by the app");
        require(receivers.length == proportions.length, "MFA: number receivers not equal flowRates");
        (,,address sender,,) = _host.decodeCtx(ctx);
        require(_userFlows[sender].length == 0, "MFA: Multiflow alread created");

        newCtx = _host.chargeGasFee(ctx, 30000);

        (, int256 receivingFlowRate, ,) = _constantFlow.getFlow(
            superToken,
            sender,
            address(this)
        );
        require(receivingFlowRate == 0, "MFA: Updates are not supported, go to YAM");

        for(uint256 i = 0; i < receivers.length; i++) {
            _userFlows[sender].push(ReceiverData(receivers[i], proportions[i]));
        }
    }

    function _updateMultiFlow(
        ISuperToken superToken,
        address sender,
        bytes4 selector,
        int96 receivingFlowRate,
        bytes calldata ctx
    )
        private
        returns (bytes memory newCtx)
    {
        uint256 sum = _sumProportions(_userFlows[sender]);
        require(sum != 0 , "MFA: Sum is zero");

        newCtx = ctx;
        for(uint256 i = 0; i < _userFlows[sender].length; i++) {
            require(_userFlows[sender][i].proportion > 0, "Proportion > 0");
            int96 targetFlowrate = (int96(_userFlows[sender][i].proportion) * receivingFlowRate) / int96(sum);
            (newCtx, ) = _host.callAgreementWithContext(
                _constantFlow,
                abi.encodeWithSelector(
                    selector,
                    superToken,
                    _userFlows[sender][i].to,
                    targetFlowrate,
                    new bytes(0)
                ),
                newCtx
            );
        }
    }

    function afterAgreementCreated(
        ISuperToken superToken,
        bytes calldata ctx,
        address /*agreementClass*/,
        bytes32 agreementId,
        bytes calldata /*cbdata*/
    )
    external
    override
    returns(bytes memory newCtx)
    {
        (,,address sender,,) = _host.decodeCtx(ctx);
        require(_userFlows[sender].length > 0 , "MFA: Create Multi Flow first or go away");
        (, int96 receivingFlowRate, , ) = _constantFlow.getFlowByID(superToken, agreementId);

        require(receivingFlowRate != 0, "MFA: not zero pls");

        newCtx = _updateMultiFlow(superToken, sender, _constantFlow.createFlow.selector, receivingFlowRate, ctx);
    }

    function beforeAgreementUpdated(
        ISuperToken superToken,
        bytes calldata /*ctx*/,
        address agreementClass,
        bytes32 agreementId
    )
        external
        view
        override
        returns (bytes memory cbdata)
    {
        require(agreementClass == address(_constantFlow), "MFA: Unsupported agreement");
        (, int256 oldFlowRate, ,) = _constantFlow.getFlowByID(superToken, agreementId);
        return abi.encode(oldFlowRate);
    }

    function afterAgreementUpdated(
        ISuperToken superToken,
        bytes calldata ctx,
        address /*agreementClass*/,
        bytes32 agreementId,
        bytes calldata cbdata
    )
        external override
        returns (bytes memory newCtx)
    {
        (,,address sender,,) = _host.decodeCtx(ctx);
        (, int96 newFlowRate, , ) = _constantFlow.getFlowByID(superToken, agreementId);

        int96 oldFlowRate = abi.decode(cbdata, (int96));
        require(newFlowRate > oldFlowRate, "MFA: only increasing flow rate"); // Funcky logic for testing purpose

        newCtx = _updateMultiFlow(superToken, sender, _constantFlow.updateFlow.selector, newFlowRate, ctx);
    }

    function afterAgreementTerminated(
        ISuperToken superToken,
        bytes calldata ctx,
        address /*agreementClass*/,
        bytes32 /*agreementId*/,
        bytes memory /*cbdata*/
    )

        external
        override
        returns (bytes memory newCtx)
    {
        (,,address sender,,) = _host.decodeCtx(ctx);
        newCtx = ctx;
        for(uint256 i = 0; i < _userFlows[sender].length; i++) {
            (newCtx, ) = _host.callAgreementWithContext(
                _constantFlow,
                abi.encodeWithSelector(
                    _constantFlow.deleteFlow.selector,
                    superToken,
                    address(this),
                    _userFlows[sender][i].to,
                    new bytes(0)
                ),
                newCtx
            );
        }
        delete _userFlows[sender];
    }

    function _unpackData(bytes memory data) internal pure returns(bytes32, int256) {
        return abi.decode(data, (bytes32, int256));
    }

    function _sumProportions(ReceiverData[] memory receivers) internal pure returns(uint256 sum) {
        for(uint256 i = 0; i < receivers.length; i++) {
            sum += receivers[i].proportion;
        }
    }
}

src/superfluid/apps/SuperAppBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >= 0.7.0;

import {
    ISuperfluid,
    ISuperToken,
    ISuperApp,
    SuperAppDefinitions
} from "../interfaces/superfluid/ISuperfluid.sol";

abstract contract SuperAppBase is ISuperApp {

    function beforeAgreementCreated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/
    )
        external
        view
        virtual
        override
        returns (bytes memory /*cbdata*/)
    {
        revert("Unsupported callback - Before Agreement Created");
    }

    function afterAgreementCreated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/,
        bytes calldata /*cbdata*/
    )
        external
        virtual
        override
        returns (bytes memory /*newCtx*/)
    {
        revert("Unsupported callback - After Agreement Created");
    }

    function beforeAgreementUpdated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/
    )
        external
        view
        virtual
        override
        returns (bytes memory /*cbdata*/)
    {
        revert("Unsupported callback - Before Agreement updated");
    }

    function afterAgreementUpdated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/,
        bytes calldata /*cbdata*/
    )
        external
        virtual
        override
        returns (bytes memory /*newCtx*/)
    {
        revert("Unsupported callback - After Agreement Updated");
    }

    function beforeAgreementTerminated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/
    )
        external
        view
        virtual
        override
        returns (bytes memory /*cbdata*/)
    {
        revert("Unsupported callback -  Before Agreement Terminated");
    }

    function afterAgreementTerminated(
        ISuperToken /*superToken*/,
        bytes calldata /*ctx*/,
        address /*agreementClass*/,
        bytes32 /*agreementId*/,
        bytes memory /*cbdata*/
    )
        external
        virtual
        override
        returns (bytes memory /*newCtx*/)
    {
        revert("Unsupported callback - After Agreement Terminated");
    }

}

src/superfluid/test/TestGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.1;

import { Ownable } from "../access/Ownable.sol";
import {
    ISuperfluid,
    ISuperfluidGovernance
} from "../interfaces/superfluid/ISuperfluid.sol";

contract TestGovernance is
    Ownable,
    ISuperfluidGovernance
{
    address private _rewardAddress;
    uint256 private _liquidationPeriod;
    address[] private _agreementList;
    mapping (address => uint) private _agreementMap;

    constructor(
        address rewardAddress,
        uint256 liquidationPeriod
    )
    {
        _owner = msg.sender;
        _rewardAddress = rewardAddress;
        _liquidationPeriod = liquidationPeriod;
    }

    function getRewardAddress(
        address /* superToken */
    )
        external
        view
        override
        returns(address rewardAddress)
    {
        return _rewardAddress;
    }

    function setRewardAddress(
        address rewardAddress
    )
        external
        onlyOwner
    {
        _rewardAddress = rewardAddress;
    }

    function getLiquidationPeriod(
        address /* superToken */
    )
        external
        view
        override
        returns(uint256 period)
    {
        return _liquidationPeriod;
    }

    function addAgreement(address agreementClass) external onlyOwner override {
        require(agreementClass != address(0), "TestGovernance: 0x address detected");
        _agreementList.push(agreementClass);
        _agreementMap[agreementClass] = _agreementList.length;
    }

    function isAgreementListed(address agreementClass) external override view returns(bool yes) {
        return _agreementMap[agreementClass] > 0;
    }

    function mapAgreements(uint256 bitmap)
        external view override
        returns (address[] memory agreementClasses) {
        uint i;
        uint n;
        // create memory output using the counted size
        agreementClasses = new address[](_agreementList.length);
        // add to the output
        n = 0;
        for (i = 0; i < _agreementList.length; ++i) {
            if ((bitmap & (1 << i)) > 0) {
                agreementClasses[n++] = _agreementList[i];
            }
        }
        // resize memory arrays
        assembly {
            mstore(agreementClasses, n)
        }
    }

    function maskAgreementBit(uint256 bitmap, address agreementClass)
        external view override
        returns (uint256 newBitmap)
    {
        require(_agreementMap[agreementClass] > 0, "TestGovernance: Agreement not listed");
        return bitmap | (1 << (_agreementMap[agreementClass] - 1));
    }

    function unmaskAgreementBit(uint256 bitmap, address agreementClass)
        external view override
        returns (uint256 newBitmap)
    {
        require(_agreementMap[agreementClass] > 0, "TestGovernance: Agreement not listed");
        return bitmap & ~(1 << (_agreementMap[agreementClass] - 1));
    }
}

